public class LongestBeautifulSubstring_5740 {

    public static int longestBeautifulSubstring(String word) {
        char[] chars = word.toCharArray();
        int startIndex = 0;
        int nextCharIndex = 0;
        int length = 0;
        char preChar = 'a';
        char[] words = new char[]{'a', 'e', 'i', 'o', 'u'};
        for (int i = 0; i < chars.length; i++) {
            char temp = chars[i];
            if (temp == words[nextCharIndex]) {
                if (temp == 'a') {
                    startIndex = i;
                }
                if (temp == 'u') {
                    length = Math.max(length, i - startIndex + 1);
                    nextCharIndex--;
                }
                nextCharIndex++;
                preChar = temp;
            } else if (preChar != temp) {
                nextCharIndex = 0;
                preChar = 'a';
                if (temp == 'a') {
                    startIndex = i;
                    nextCharIndex++;
                    preChar = temp;
                }
            }
        }
        return length;
    }

    public static void main(String[] args) {
        String word = "";
        longestBeautifulSubstring(word);
    }
}
